import heapq

TARGET_STR  = ''

solutions = []

def is_palindrome(string):
    for i in range(len(string) / 2):
        if string[i] != string[-(i+1)]:
            return False
    return True

for substr_len in range(len(TARGET_STR)):
    for i in range(len(TARGET_STR) - substr_len):
        substr = TARGET_STR[i:i + substr_len - 1]
        if is_palindrome(substr):
            solutions.append((substr_len, substr))


